import React, { useEffect } from "react";
import styles from "./MomentList.module.scss";

const MomentList = ({ moments }) => {
  const buyItNow = (moment) => {
    return (
      moment.discountValue > 100 && moment.listings.cheapestListingPrice < 95
    );
  };

  const beep = () => {
    const snd = new Audio(
      "data:audio/wav;base64,"
    );
    snd.play();
  };

  useEffect(() => {
    setTimeout(() => {
      if (
        moments.length > 0 &&
        moments[0].discountValue > 100 &&
        moments[0].listings.cheapestListingPrice < 95
      ) {
        setTimeout(() => {
          beep();
        }, 1);
        setTimeout(() => {
          beep();
        }, 101);
        setTimeout(() => {
          beep();
        }, 201);
        setTimeout(() => {
          beep();
        }, 301);
      }
    }, 0);
  }, [moments]);

  return (
    <ul>
      <li className={`${styles.momentListItem} ${styles.momentListItemHeader}`}>
        <div className={styles.playerName}>Player Name</div>
        <div className={styles.discountValue}>Discount Value</div>
        <div className={styles.buyNow}>Purchase Player</div>
        <div className={styles.estValue}>Est Value</div>
        <div className={styles.listPrice}>List Price</div>
        <div className={styles.seeAll}>See All</div>
      </li>
      {moments.map((moment) => {
        const { metadata } = moment;
        if (!moment.metadata.playerName) {
          return null;
        }
        return (
          <li className={styles.momentListItem}>
            <div className={styles.playerName}>
              <div>{metadata.playerName}</div>
              <div className={styles.serialNumber}>
                <span
                  className={`${
                    metadata.serialNumber < 100 ? styles.lowSerial : ""
                  }`}
                >
                  #{metadata.serialNumber}
                </span>{" "}
                / {metadata.currentMintSize}
              </div>
            </div>
            <div
              className={`${
                buyItNow(moment) ? styles.discountBaby : styles.discountValue
              }`}
            >
              {moment.discountValue}%
            </div>
            <div
              className={`${
                buyItNow(moment) ? styles.hurryBuy : styles.buyNow
              }`}
            >
              <a
                href={`https://marketplace.nflallday.com/edition/${moment.metadata.editionId}/moment/${moment.tokenId}`}
                target="_blank"
              >
                Buy Me Now
              </a>
            </div>
            <div className={styles.estValue}>
              * ${moment.valuations.MRValue}
            </div>
            <div className={styles.listPrice}>
              ${moment.listings.cheapestListingPrice}
            </div>

            <div className={styles.seeAll}>
              <a
                href={`https://marketplace.nflallday.com/edition/${moment.metadata.editionId}`}
                target="_blank"
              >
                See all for Sale
              </a>
            </div>
          </li>
        );
      })}
    </ul>
  );
};

export default MomentList;
